// src/components/ShopPerformance.jsx
import React, { useEffect, useState } from 'react';

const ShopPerformance = () => {
  const [shopData, setShopData] = useState({
    gmv: 0,
    orders: 0,
    unitsSold: 0,
    loading: true,
    error: null,
  });

  // Fetch data from the backend
  useEffect(() => {
    const fetchShopData = async () => {
      try {
        const start_time = Math.floor(Date.now() / 1000) - 86400; // 1 day ago (in UNIX time)
        const end_time = Math.floor(Date.now() / 1000); // Current time (in UNIX time)
        const page_size = 10;
        const page_no = 1;

        const response = await fetch(
          `http://localhost:8000/shop/performance?start_time=${start_time}&end_time=${end_time}&page_size=${page_size}&page_no=${page_no}`
        );
        const data = await response.json();

        if (data.gmv) {
          setShopData({ ...data, loading: false });
        } else {
          throw new Error('No data returned');
        }
      } catch (error) {
        console.error('Error fetching shop data:', error);
        setShopData({ ...shopData, loading: false, error: error.message });
      }
    };

    fetchShopData();
  }, []);

  if (shopData.loading) {
    return <p>Loading...</p>;
  }

  if (shopData.error) {
    return <p>Error: {shopData.error}</p>;
  }

  return (
    <div>
      <h2>Shop Performance Overview</h2>
      {/* Displaying Cards for GMV, Orders, Units Sold */}
      <div className="cards-container">
        <div className="card">
          <h3>Total GMV</h3>
          <p>{shopData.gmv} USD</p>
        </div>
        <div className="card">
          <h3>Total Orders</h3>
          <p>{shopData.orders}</p>
        </div>
        <div className="card">
          <h3>Units Sold</h3>
          <p>{shopData.unitsSold}</p>
        </div>
      </div>
    </div>
  );
};

export default ShopPerformance;
