import React, { useState, useEffect } from 'react';
import './InventoryTracker.css';

const InventoryTracker = () => {
  const [brands, setBrands] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState('');
  const [skus, setSkus] = useState([]);
  const [lowStockItems, setLowStockItems] = useState([]);
  const [alertHistory, setAlertHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [source, setSource] = useState('database');
  const [stats, setStats] = useState({
    totalSkus: 0,
    lowStockCount: 0,
    outOfStockCount: 0,
    alertsSentToday: 0
  });

  const API_BASE = 'http://localhost:8000';

  // Fetch brands on component mount
  useEffect(() => {
    fetchBrands();
    fetchAlertHistory();
  }, []);

  // Fetch SKUs when brand changes
  useEffect(() => {
    if (selectedBrand) {
      fetchSkus(selectedBrand);
    }
  }, [selectedBrand]);

  const fetchBrands = async () => {
    try {
      const response = await fetch(`${API_BASE}/inventory/brands`);
      const data = await response.json();
      if (data.status === 'success') {
        setBrands(data.brands);
        if (data.brands.length > 0) {
          setSelectedBrand(data.brands[0].id.toString());
        }
      }
    } catch (error) {
      console.error('Error fetching brands:', error);
    }
  };

  const fetchSkus = async (brandId) => {
    try {
      const response = await fetch(`${API_BASE}/inventory/skus?brand_id=${brandId}`);
      const data = await response.json();
      if (data.status === 'success') {
        setSkus(data.skus);
        calculateStats(data.skus);
      }
    } catch (error) {
      console.error('Error fetching SKUs:', error);
    }
  };

  const calculateStats = (skuData) => {
    const totalSkus = skuData.length;
    const lowStockCount = skuData.filter(sku => 
      sku.stock_count <= sku.low_stock_threshold && sku.stock_count > 0
    ).length;
    const outOfStockCount = skuData.filter(sku => sku.stock_count === 0).length;

    setStats({
      totalSkus,
      lowStockCount,
      outOfStockCount,
      alertsSentToday: alertHistory.filter(alert => {
        const today = new Date().toDateString();
        const alertDate = new Date(alert.sent_at).toDateString();
        return today === alertDate;
      }).length
    });
  };

  const checkStock = async (sendAlerts = true) => {
    if (!selectedBrand) return;

    setLoading(true);
    try {
      const response = await fetch(
        `${API_BASE}/inventory/check-stock?brand_id=${selectedBrand}&send_alerts=${sendAlerts}&source=${source}`
      );
      const data = await response.json();

      if (data.status === 'success') {
        setLowStockItems(data.low_stock_items);
        if (sendAlerts) {
          fetchAlertHistory(); // Refresh alert history
        }
      }
    } catch (error) {
      console.error('Error checking stock:', error);
    } finally {
      setLoading(false);
    }
  };

  const checkAllBrands = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE}/inventory/check-all-brands?send_alerts=true`);
      const data = await response.json();

      if (data.status === 'success') {
        // Combine all low stock items from all brands
        const allLowStock = data.results.flatMap(result => result.low_stock_items);
        setLowStockItems(allLowStock);
        fetchAlertHistory();
      }
    } catch (error) {
      console.error('Error checking all brands:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchAlertHistory = async () => {
    try {
      const brandParam = selectedBrand ? `&brand_id=${selectedBrand}` : '';
      const response = await fetch(`${API_BASE}/inventory/alerts/history?limit=50&days=7${brandParam}`);
      const data = await response.json();

      if (data.status === 'success') {
        setAlertHistory(data.alerts);
      }
    } catch (error) {
      console.error('Error fetching alert history:', error);
    }
  };

  const updateStock = async (skuId, newStock) => {
    try {
      const response = await fetch(
        `${API_BASE}/inventory/update-stock?sku_id=${skuId}&stock_count=${newStock}`,
        { method: 'POST' }
      );
      const data = await response.json();

      if (data.status === 'success') {
        fetchSkus(selectedBrand); // Refresh SKUs
      }
    } catch (error) {
      console.error('Error updating stock:', error);
    }
  };

  const updateThreshold = async (skuId, newThreshold) => {
    try {
      const response = await fetch(
        `${API_BASE}/inventory/update-threshold?sku_id=${skuId}&threshold=${newThreshold}`,
        { method: 'POST' }
      );
      const data = await response.json();

      if (data.status === 'success') {
        fetchSkus(selectedBrand); // Refresh SKUs
      }
    } catch (error) {
      console.error('Error updating threshold:', error);
    }
  };

  const testSlack = async () => {
    try {
      const response = await fetch(
        `${API_BASE}/inventory/test-slack?brand_id=${selectedBrand}`,
        { method: 'POST' }
      );
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error('Error testing Slack:', error);
    }
  };

  const startMonitoring = async () => {
    try {
      const response = await fetch(
        `${API_BASE}/inventory/start-monitoring?interval_minutes=60`,
        { method: 'POST' }
      );
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error('Error starting monitoring:', error);
    }
  };

  const getStockStatus = (stock, threshold) => {
    if (stock === 0) return 'out-of-stock';
    if (stock <= threshold) return 'low-stock';
    return 'in-stock';
  };

  const getStockStatusColor = (status) => {
    switch (status) {
      case 'out-of-stock': return '#dc3545';
      case 'low-stock': return '#ffc107';
      case 'in-stock': return '#28a745';
      default: return '#6c757d';
    }
  };

  return (
    <div className="inventory-tracker">
      <header className="header">
        <h1>📦 Inventory Tracker</h1>
        <div className="header-controls">
          <select 
            value={selectedBrand} 
            onChange={(e) => setSelectedBrand(e.target.value)}
            className="brand-selector"
          >
            <option value="">Select Brand</option>
            {brands.map(brand => (
              <option key={brand.id} value={brand.id}>
                {brand.name}
              </option>
            ))}
          </select>

          <select 
            value={source} 
            onChange={(e) => setSource(e.target.value)}
            className="source-selector"
          >
            <option value="database">Database</option>
            <option value="tiktok">TikTok API</option>
          </select>

          <button onClick={testSlack} className="btn btn-info">
            🔔 Test Slack
          </button>
        </div>
      </header>

      <nav className="nav-tabs">
        {['dashboard', 'inventory', 'alerts', 'settings'].map(tab => (
          <button
            key={tab}
            className={`nav-tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </nav>

      <main className="main-content">
        {activeTab === 'dashboard' && (
          <div className="dashboard">
            <div className="stats-grid">
              <div className="stat-card">
                <h3>Total SKUs</h3>
                <div className="stat-value">{stats.totalSkus}</div>
              </div>
              <div className="stat-card low-stock">
                <h3>Low Stock</h3>
                <div className="stat-value">{stats.lowStockCount}</div>
              </div>
              <div className="stat-card out-of-stock">
                <h3>Out of Stock</h3>
                <div className="stat-value">{stats.outOfStockCount}</div>
              </div>
              <div className="stat-card alerts">
                <h3>Alerts Today</h3>
                <div className="stat-value">{stats.alertsSentToday}</div>
              </div>
            </div>

            <div className="actions">
              <button 
                onClick={() => checkStock(true)} 
                disabled={loading || !selectedBrand}
                className="btn btn-primary"
              >
                {loading ? '🔄 Checking...' : '🔍 Check Stock & Send Alerts'}
              </button>

              <button 
                onClick={() => checkStock(false)} 
                disabled={loading || !selectedBrand}
                className="btn btn-secondary"
              >
                📊 Check Stock Only
              </button>

              <button 
                onClick={checkAllBrands} 
                disabled={loading}
                className="btn btn-warning"
              >
                🏢 Check All Brands
              </button>

              <button 
                onClick={startMonitoring} 
                className="btn btn-success"
              >
                ⏰ Start Auto Monitoring
              </button>
            </div>

            {lowStockItems.length > 0 && (
              <div className="low-stock-section">
                <h2>🚨 Items Needing Attention ({lowStockItems.length})</h2>
                <div className="low-stock-grid">
                  {lowStockItems.map((item, index) => (
                    <div key={index} className={`low-stock-item ${item.alert_level}`}>
                      <div className="item-header">
                        <strong>{item.sku_name}</strong>
                        <span className={`status-badge ${item.alert_level}`}>
                          {item.alert_level === 'out_of_stock' ? '🚨 OUT OF STOCK' : '⚠️ LOW STOCK'}
                        </span>
                      </div>
                      <div className="item-details">
                        <p>Current: {item.current_stock}</p>
                        <p>Threshold: {item.threshold}</p>
                        <p>Priority: <span className="priority">{item.priority}</span></p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'inventory' && (
          <div className="inventory-tab">
            <div className="inventory-header">
              <h2>📋 Inventory Management</h2>
              <button 
                onClick={() => fetchSkus(selectedBrand)} 
                className="btn btn-secondary"
                disabled={!selectedBrand}
              >
                🔄 Refresh
              </button>
            </div>

            <div className="inventory-table-container">
              <table className="inventory-table">
                <thead>
                  <tr>
                    <th>SKU Name</th>
                    <th>Current Stock</th>
                    <th>Threshold</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {skus.map(sku => {
                    const status = getStockStatus(sku.stock_count, sku.low_stock_threshold);
                    return (
                      <tr key={sku.id} className={`sku-row ${status}`}>
                        <td className="sku-name">{sku.name}</td>
                        <td className="stock-count">
                          <input
                            type="number"
                            value={sku.stock_count}
                            onChange={(e) => {
                              const newStock = parseInt(e.target.value) || 0;
                              updateStock(sku.id, newStock);
                            }}
                            className="stock-input"
                            min="0"
                          />
                        </td>
                        <td className="threshold">
                          <input
                            type="number"
                            value={sku.low_stock_threshold}
                            onChange={(e) => {
                              const newThreshold = parseInt(e.target.value) || 0;
                              updateThreshold(sku.id, newThreshold);
                            }}
                            className="threshold-input"
                            min="0"
                          />
                        </td>
                        <td className="status">
                          <span 
                            className="status-indicator"
                            style={{ backgroundColor: getStockStatusColor(status) }}
                          >
                            {status === 'out-of-stock' ? '🚨 Out' : 
                             status === 'low-stock' ? '⚠️ Low' : '✅ OK'}
                          </span>
                        </td>
                        <td className="actions">
                          <button
                            onClick={() => {
                              const newStock = prompt('Enter new stock count:', sku.stock_count);
                              if (newStock !== null) {
                                updateStock(sku.id, parseInt(newStock) || 0);
                              }
                            }}
                            className="btn btn-sm btn-primary"
                          >
                            📝 Edit
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'alerts' && (
          <div className="alerts-tab">
            <div className="alerts-header">
              <h2>🔔 Alert History</h2>
              <button 
                onClick={fetchAlertHistory} 
                className="btn btn-secondary"
              >
                🔄 Refresh
              </button>
            </div>

            <div className="alerts-list">
              {alertHistory.length === 0 ? (
                <div className="no-alerts">
                  <p>No alerts found for the selected criteria.</p>
                </div>
              ) : (
                alertHistory.map(alert => (
                  <div key={alert.id} className="alert-item">
                    <div className="alert-header">
                      <span className="alert-type">
                        {alert.alert_type === 'low_inventory' ? '📉' : '🚨'} 
                        {alert.alert_type.replace('_', ' ').toUpperCase()}
                      </span>
                      <span className="alert-time">
                        {new Date(alert.sent_at).toLocaleString()}
                      </span>
                    </div>
                    <div className="alert-message">
                      {alert.message}
                    </div>
                    <div className="alert-footer">
                      <span className="delivery-method">
                        📨 {alert.delivery_method}
                      </span>
                      <span className="brand-id">
                        Brand ID: {alert.brand_id}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'settings' && (
          <div className="settings-tab">
            <h2>⚙️ Settings</h2>

            <div className="settings-section">
              <h3>Slack Integration</h3>
              <div className="setting-item">
                <label>Test Slack Integration:</label>
                <button onClick={testSlack} className="btn btn-info">
                  🔔 Send Test Alert
                </button>
              </div>
            </div>

            <div className="settings-section">
              <h3>Monitoring</h3>
              <div className="setting-item">
                <label>Auto Monitoring:</label>
                <button onClick={startMonitoring} className="btn btn-success">
                  ⏰ Start Background Monitoring
                </button>
                <small>Runs every 60 minutes for all brands</small>
              </div>
            </div>

            <div className="settings-section">
              <h3>Data Source</h3>
              <div className="setting-item">
                <label>Inventory Source:</label>
                <select 
                  value={source} 
                  onChange={(e) => setSource(e.target.value)}
                  className="source-selector"
                >
                  <option value="database">Database (SKUs Table)</option>
                  <option value="tiktok">TikTok Shop API</option>
                </select>
                <small>
                  Database: Uses your local SKUs table<br/>
                  TikTok: Fetches live data from TikTok Shop API
                </small>
              </div>
            </div>

            <div className="settings-section">
              <h3>Debug Tools</h3>
              <div className="setting-item">
                <label>TikTok API Debug:</label>
                <button 
                  onClick={async () => {
                    try {
                      const response = await fetch(`${API_BASE}/inventory/debug-tiktok`);
                      const data = await response.json();
                      console.log('TikTok API Debug:', data);
                      alert('Debug data logged to console. Check browser dev tools.');
                    } catch (error) {
                      alert('Error debugging TikTok API: ' + error.message);
                    }
                  }}
                  className="btn btn-warning"
                >
                  🐛 Debug TikTok API
                </button>
                <small>Logs TikTok API response structure to console</small>
              </div>
            </div>

            <div className="settings-section">
              <h3>Brand Management</h3>
              <div className="setting-item">
                <label>Selected Brand:</label>
                <select 
                  value={selectedBrand} 
                  onChange={(e) => setSelectedBrand(e.target.value)}
                  className="brand-selector"
                >
                  <option value="">Select Brand</option>
                  {brands.map(brand => (
                    <option key={brand.id} value={brand.id}>
                      {brand.name} (ID: {brand.id})
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>
        )}
      </main>

      {loading && (
        <div className="loading-overlay">
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Processing...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default InventoryTracker;
