import React, { useState } from 'react';
import { 
  BarChart3, 
  Package, 
  PieChart, 
  Users, 
  TrendingUp, 
  Settings, 
  Zap,
  FolderOpen,
  LogOut,
  Package2,
  ShoppingCart,
  Video,
  Database,
  AlertTriangle,
  TrendingDown
} from 'lucide-react';

const Sidebar = () => {
  const [userRole, setUserRole] = useState('Brand Admin'); // Can be 'Super Admin' or 'Brand Admin'

  // Navigation items mapped to your modules
  const navigationItems = [
    {
      id: 'dashboard',
      name: 'Dashboard',
      href: '/',
      icon: BarChart3,
      active: true,
      module: 'Dashboard Overview',
      description: 'View metrics by brand or global'
    },
    {
      id: 'shop-performance',
      name: 'Shop Performance',
      href: '/shop-performance',
      icon: ShoppingCart,
      module: 'Shop Performance Module',
      description: 'GMV, TikTok campaigns, refund rates'
    },
    {
      id: 'creator-performance',
      name: 'Creator Performance',
      href: '/creator-performance',
      icon: Video,
      module: 'Creator Performance Module',
      description: 'Creator metrics and content performance'
    },
    {
      id: 'products',
      name: 'Products',
      href: '/products',
      icon: Package,
      module: 'Backend Metrics Module',
      description: 'Inventory and top SKUs'
    },
    {
      id: 'ceo-dashboard',
      name: 'CEO Dashboard',
      href: '/ceo-dashboard',
      icon: PieChart,
      module: 'Dashboard Overview',
      description: 'Executive level metrics',
      superAdminOnly: true
    },
    {
      id: 'customer-insight',
      name: 'Customer Insight',
      href: '/customer-insight',
      icon: Users,
      module: 'Creator Performance Module',
      description: 'Customer and affiliate insights'
    },
    {
      id: 'backend-metrics',
      name: 'Backend Metrics',
      href: '/backend-metrics',
      icon: Database,
      module: 'Backend Metrics Module',
      description: 'Inventory alerts and sample distribution'
    },
    {
      id: 'cogs-fbs-values',
      name: 'COGS and FBS Values',
      href: '/cogs-fbs-values',
      icon: TrendingUp,
      module: 'Shop Performance Module',
      description: 'Cost analysis and FBS tracking'
    }
  ];

  const settingsItems = [
    {
      id: 'resources',
      name: 'Resources',
      href: '/resources',
      icon: FolderOpen
    },
    {
      id: 'account-settings',
      name: 'Account Settings',
      href: '/account-settings',
      icon: Settings
    },
    {
      id: 'setup-wizard',
      name: 'Set-Up Wizard',
      href: '/setup-wizard',
      icon: Zap
    }
  ];

  const bottomItems = [
    {
      id: 'affiliate-program',
      name: 'Affiliate Program',
      href: '/affiliate-program',
      icon: Users
    },
    {
      id: 'logout',
      name: 'Logout',
      href: '/logout',
      icon: LogOut
    },
    {
      id: 'inverted',
      name: 'inverted',
      href: '/inverted',
      icon: Package2
    }
  ];

  // Filter items based on user role
  const filteredNavItems = navigationItems.filter(item => 
    !item.superAdminOnly || userRole === 'Super Admin'
  );

  return (
    <div className="w-64 bg-white border-r border-gray-200 h-screen flex flex-col">
      {/* Logo Section */}
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center space-x-2">
          <div className="w-8 h-8 bg-orange-400 rounded-lg flex items-center justify-center">
            <span className="text-white font-bold text-sm">#</span>
          </div>
          <span className="text-xl font-bold text-gray-800">TT_Dashboard</span>
        </div>
      </div>

      {/* Account Section */}
      <div className="px-6 py-4 border-b border-gray-200">
        <h3 className="text-sm font-medium text-gray-600 mb-2">My Account</h3>
        <div className="flex items-center justify-between">
          <span className="text-xs text-gray-500">Role: {userRole}</span>
          <button
            onClick={() => setUserRole(userRole === 'Super Admin' ? 'Brand Admin' : 'Super Admin')}
            className="text-xs text-orange-600 hover:text-orange-700 underline"
          >
            Switch
          </button>
        </div>
      </div>

      {/* Navigation Links */}
      <nav className="flex-1 px-4 py-2 overflow-y-auto">
        {/* Main Navigation */}
        <div className="mb-6">
          <h4 className="px-3 mb-2 text-xs font-semibold text-gray-500 uppercase tracking-wide">
            Main Navigation
          </h4>
          <ul className="space-y-1">
            {filteredNavItems.map((item) => {
              const Icon = item.icon;
              return (
                <li key={item.id}>
                  <a 
                    href={item.href}
                    className={`flex items-center px-3 py-2 text-sm font-medium rounded-lg group ${
                      item.active 
                        ? 'text-orange-600 bg-orange-50'
                        : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                    }`}
                    title={item.description}
                  >
                    <Icon className="w-4 h-4 mr-3" />
                    {item.name}
                    {item.superAdminOnly && (
                      <span className="ml-auto text-xs bg-orange-100 text-orange-600 px-1 rounded">
                        SA
                      </span>
                    )}
                  </a>
                </li>
              );
            })}
          </ul>
        </div>

        {/* Settings Section */}
        <div className="mb-6">
          <h4 className="px-3 mb-2 text-xs font-semibold text-gray-500 uppercase tracking-wide">
            Settings
          </h4>
          <ul className="space-y-1">
            {settingsItems.map((item) => {
              const Icon = item.icon;
              return (
                <li key={item.id}>
                  <a 
                    href={item.href}
                    className="flex items-center px-3 py-2 text-sm font-medium text-gray-600 hover:text-gray-900 hover:bg-gray-50 rounded-lg group"
                  >
                    <Icon className="w-4 h-4 mr-3" />
                    {item.name}
                  </a>
                </li>
              );
            })}
          </ul>
        </div>

        {/* Shop Data Sync Progress */}
        <div className="mb-6 px-3">
          <div className="text-xs text-gray-500 mb-2 flex items-center">
            <Database className="w-3 h-3 mr-1" />
            Shop Data Sync
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2">
            <div className="bg-orange-400 h-2 rounded-full" style={{width: '65%'}}></div>
          </div>
          <div className="text-xs text-gray-500 mt-1">65% Complete</div>
        </div>

        {/* System Alerts */}
        <div className="mb-4 px-3">
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
            <div className="flex items-center text-xs text-yellow-800 mb-1">
              <AlertTriangle className="w-3 h-3 mr-1" />
              System Alerts
            </div>
            <div className="text-xs text-yellow-700">
              • 5 low stock items
              <br />
              • 2 pending samples
            </div>
          </div>
        </div>
      </nav>

      {/* Subscription Section */}
      <div className="p-4 border-t border-gray-200">
        {/* Bottom Navigation Items */}
        <ul className="space-y-1">
          {bottomItems.map((item) => {
            const Icon = item.icon;
            return (
              <li key={item.id}>
                <a 
                  href={item.href}
                  className="flex items-center px-3 py-2 text-sm font-medium text-gray-600 hover:text-gray-900 hover:bg-gray-50 rounded-lg group"
                >
                  <Icon className="w-4 h-4 mr-3" />
                  {item.name}
                </a>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
