import React from 'react';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const navigate = useNavigate();

  // Function to handle the login with TikTok redirect
  const handleLoginWithTikTok = () => {
    const clientKey = '6fhicd5f13erg'; // Replace with your TikTok App Key
    const redirectUri = 'http://localhost:3000/tiktok/callback'; // Your app's callback URL
    const scope = 'user.info.basic'; // Scope your app needs
    const state = 'random_state_string'; // Optional: to prevent CSRF attacks

    // Construct TikTok's OAuth URL
    const authUrl = `https://open-api.tiktok.com/platform/oauth/connect/?client_key=${clientKey}&response_type=code&scope=${scope}&redirect_uri=${redirectUri}&state=${state}`;

    // Redirect to TikTok's OAuth page
    window.location.href = authUrl;
  };

  return (
    <div className="login-container">
      <h2>Login with TikTok</h2>
      <button onClick={handleLoginWithTikTok}>Login with TikTok</button>
    </div>
  );
};

export default LoginPage;
