import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Dashboard from './Dashboard';  // Assuming you exported the Dashboard component
import ShopPerformance from './ShopPerformance'; // Assuming you exported ShopPerformance component
import CreatorPerformance from './CreatorPerformance'; // Assuming you exported CreatorPerformance component

const ShopDetailPage = () => {
  const { brandId } = useParams(); // Get the brand ID from the URL
  const [dashboardData, setDashboardData] = useState(null);
  const [shopPerformanceData, setShopPerformanceData] = useState(null);
  const [creatorPerformanceData, setCreatorPerformanceData] = useState(null);

  // Fetch data for all three tables based on the selected brandId
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch dashboard data
        const dashboardResponse = await axios.get(`/admin/dashboard/${brandId}`);
        setDashboardData(dashboardResponse.data);

        // Fetch shop performance data
        const shopPerformanceResponse = await axios.get(`/admin/shop-performance/${brandId}`);
        setShopPerformanceData(shopPerformanceResponse.data);

        // Fetch creator performance data
        const creatorPerformanceResponse = await axios.get(`/admin/creator-performance/${brandId}`);
        setCreatorPerformanceData(creatorPerformanceResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [brandId]);

  if (!dashboardData || !shopPerformanceData || !creatorPerformanceData) {
    return <div>Loading...</div>; // Loading state while data is being fetched
  }

  return (
    <div className="shop-detail-page">
      <h1>{dashboardData.brandName} Shop Details</h1>

      {/* Dashboard Table */}
      <h2>Dashboard</h2>
      <Dashboard data={dashboardData} />

      {/* Shop Performance Table */}
      <h2>Shop Performance</h2>
      <ShopPerformance data={shopPerformanceData} />

      {/* Creator Performance Table */}
      <h2>Creator Performance</h2>
      <CreatorPerformance data={creatorPerformanceData} />
    </div>
  );
};

export default ShopDetailPage;
