import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "./Dashboard.css";
import DatePickerCard from './Datepicker';

const Dashboard = () => {
  const [yesterdayData, setYesterdayData] = useState({});
  const [currentWeekData, setCurrentWeekData] = useState({});
  const [monthToDateData, setMonthToDateData] = useState({});
  const [dates, setDates] = useState({ startDate: '', endDate: '' });
  const [data, setData] = useState(null);
  const [customRangeData, setCustomRangeData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const fetchPerformance = async () => {
    if (!dates.startDate || !dates.endDate) {
      alert('Please select both start and end dates');
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.get('http://127.0.0.1:8000/shop/performance', {
        params: {
          start_time: dates.startDate,
          end_time: dates.endDate,
          page_size: 10,
          page_no: 1,
        },
      });
      setData(response.data);

      // Fetch data for the custom date range table
      await fetchCustomRangeData();
    } catch (error) {
      console.error('API call error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchCustomRangeData = async () => {
    if (!dates.startDate || !dates.endDate) return;

    try {
      const dateRange = generateDateRange(dates.startDate, dates.endDate);
      const customData = [];

      for (const date of dateRange) {
        const formattedDate = formatDate(new Date(date));

        // Fetch all required data for each date
        const [
          performanceResponse,
          flashSalesResponse,
          creatorsResponse,
          gmvPerVideoResponse,
          refundRateResponse
        ] = await Promise.all([
          axios.get('http://127.0.0.1:8000/shop/performance', {
            params: { start_time: formattedDate, end_time: formattedDate, page_size: 10, page_no: 1 }
          }),
          axios.get('http://127.0.0.1:8000/flash_sales_performance', {
            params: { start_time: formattedDate, end_time: formattedDate, page_size: 10, page_no: 1 }
          }),
          axios.get('http://127.0.0.1:8000/top_performing_creators', {
            params: { start_time: formattedDate, end_time: formattedDate, page_size: 10, page_no: 1 }
          }),
          axios.get('http://127.0.0.1:8000/Gmv_per_video', {
            params: { start_time: formattedDate, end_time: formattedDate, page_size: 10, page_no: 1 }
          }),
          axios.get('http://127.0.0.1:8000/calculate_refund_rate', {
            params: { start_time: formattedDate, end_time: formattedDate, page_size: 10, page_no: 1 }
          })
        ]);

        customData.push({
          date: formattedDate,
          totalGmv: performanceResponse.data.gmv || 0,
          flashSalesPerformance: flashSalesResponse.data.total_sales || 0,
          topPerformingCreators: creatorsResponse.data.top_creators?.length || 0,
          gmvPerVideo: gmvPerVideoResponse.data.gmv || 0,
          refundRate: refundRateResponse.data.refund_rate || 0,
          tiktokCampaign: performanceResponse.data.gmv || 0 // Using same GMV data for TikTok campaign
        });
      }

      setCustomRangeData(customData);
    } catch (error) {
      console.error('Error fetching custom range data:', error);
    }
  };

  const generateDateRange = (startDate, endDate) => {
    const dates = [];
    const start = new Date(startDate);
    const end = new Date(endDate);

    for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
      dates.push(new Date(d));
    }

    return dates;
  };

  // Helper function to format dates as 'YYYY-MM-DD'
  const formatDate = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  // Helper function to get the start of the current week (Monday)
  const getStartOfWeek = (date) => {
    const startOfWeek = new Date(date);
    const day = startOfWeek.getDay();
    const diff = startOfWeek.getDate() - day + (day === 0 ? -6 : 1);
    startOfWeek.setDate(diff);
    return formatDate(startOfWeek);
  };

  // Helper function to get the start of the current month
  const getStartOfMonth = (date) => {
    const startOfMonth = new Date(date);
    startOfMonth.setDate(1);
    return formatDate(startOfMonth);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const currentDate = new Date();

        // Get yesterday's date
        const yesterday = new Date(currentDate);
        yesterday.setDate(currentDate.getDate() - 1);
        const formattedYesterday = formatDate(yesterday);

        // Get start and end of current week
        const startOfWeek = getStartOfWeek(currentDate);
        const formattedEndOfWeek = formatDate(new Date(currentDate));

        // Get start of the current month
        const startOfMonth = getStartOfMonth(currentDate);
        const formattedEndOfMonth = formatDate(currentDate);

        // Fetch performance data for yesterday
        const yesterdayPerformanceResponse = await axios.get('http://127.0.0.1:8000/shop/performance', {
          params: {
            start_time: formattedYesterday,
            end_time: formattedYesterday,
            page_size: 10,
            page_no: 1
          }
        });

        // Fetch performance data for the current week
        const currentWeekPerformanceResponse = await axios.get('http://127.0.0.1:8000/shop/performance', {
          params: {
            start_time: startOfWeek,
            end_time: formattedEndOfWeek,
            page_size: 10,
            page_no: 1
          }
        });

        // Fetch performance data for the current month
        const monthToDatePerformanceResponse = await axios.get('http://127.0.0.1:8000/shop/performance', {
          params: {
            start_time: startOfMonth,
            end_time: formattedEndOfMonth,
            page_size: 10,
            page_no: 1
          }
        });

        // Fetch flash sales performance 
        const yesterdayflashsales = await axios.get('http://127.0.0.1:8000/flash_sales_performance', {
          params: {
            start_time: formattedYesterday,
            end_time: formattedYesterday,
            page_size: 10,
            page_no: 1
          }
        });

        const currentWeekflashsales = await axios.get('http://127.0.0.1:8000/flash_sales_performance', {
          params: {
            start_time: startOfWeek,
            end_time: formattedEndOfWeek,
            page_size: 10,
            page_no: 1
          }
        });

        const monthToDateflashsales = await axios.get('http://127.0.0.1:8000/flash_sales_performance', {
          params: {
            start_time: startOfMonth,
            end_time: formattedEndOfMonth,
            page_size: 10,
            page_no: 1
          }
        });

        // Fetch top-performing creators for each period
        const dailycreatorsResponse = await axios.get('http://127.0.0.1:8000/top_performing_creators', {
          params: {
            start_time: formattedYesterday,
            end_time: formattedYesterday,
            page_size: 10,
            page_no: 1
          }
        });
        const weaklycreatorsResponse = await axios.get('http://127.0.0.1:8000/top_performing_creators', {
          params: {
            start_time: startOfWeek,
            end_time: formattedEndOfWeek,
            page_size: 10,
            page_no: 1
          }
        });
        const monthlycreatorsResponse = await axios.get('http://127.0.0.1:8000/top_performing_creators', {
          params: {
            start_date: startOfMonth,
            end_date: formattedEndOfMonth,
            page_size: 10,
            page_no: 1
          }
        });

        const dailygmvPervideo = await axios.get('http://127.0.0.1:8000/Gmv_per_video', {
          params: {
            start_time: formattedYesterday,
            end_time: formattedYesterday,
            page_size: 10,
            page_no: 1
          }
        });
        const weeklygmvPervideo = await axios.get('http://127.0.0.1:8000/Gmv_per_video', {
          params: {
            start_time: startOfWeek,
            end_time: formattedEndOfWeek,
            page_size: 10,
            page_no: 1
          }
        });
        const monthlygmvPervideo = await axios.get('http://127.0.0.1:8000/Gmv_per_video', {
          params: {
            start_date: startOfMonth,
            end_date: formattedEndOfMonth,
            page_size: 10,
            page_no: 1
          }
        });
        const dailyRefundrate = await axios.get('http://127.0.0.1:8000/calculate_refund_rate', {
          params: {
            start_time: formattedYesterday,
            end_time: formattedYesterday,
            page_size: 10,
            page_no: 1
          }
        });
        const weeklyRefundrate = await axios.get('http://127.0.0.1:8000/calculate_refund_rate', {
          params: {
            start_time: startOfWeek,
            end_time: formattedEndOfWeek,
            page_size: 10,
            page_no: 1
          }
        });
        const monthlyRefundrate = await axios.get('http://127.0.0.1:8000/calculate_refund_rate', {
          params: {
            start_date: startOfMonth,
            end_date: formattedEndOfMonth,
            page_size: 10,
            page_no: 1
          }
        });

        // Update the dashboard state with the fetched data
        setYesterdayData({
          totalGmv: yesterdayPerformanceResponse.data.gmv || 0,
          flashSalesPerformance: yesterdayflashsales.data.total_sales || 0,
          topPerformingCreators: dailycreatorsResponse.data.top_creators?.length || 0,
          gmvpervideo: dailygmvPervideo.data.gmv || 0,
          refundrate: dailyRefundrate.data.refund_rate || 0,
          Tiktokcompaign: yesterdayPerformanceResponse.data.gmv || 0
        });

        setCurrentWeekData({
          totalGmv: currentWeekPerformanceResponse.data.gmv || 0,
          flashSalesPerformance: currentWeekflashsales.data.total_sales || 0,
          topPerformingCreators: weaklycreatorsResponse.data.top_creators?.length || 0,
          gmvpervideo: weeklygmvPervideo.data.gmv || 0,
          refundrate: weeklyRefundrate.data.refund_rate || 0,
          Tiktokcompaign: currentWeekPerformanceResponse.data.gmv || 0
        });

        setMonthToDateData({
          totalGmv: monthToDatePerformanceResponse.data.gmv || 0,
          flashSalesPerformance: monthToDateflashsales.data.total_sales || 0,
          topPerformingCreators: monthlycreatorsResponse.data.top_creators?.length || 0,
          gmvpervideo: monthlygmvPervideo.data.gmv || 0,
          refundrate: monthlyRefundrate.data.refund_rate || 0,
          Tiktokcompaign: monthToDatePerformanceResponse.data.gmv || 0
        });

      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    }).format(amount || 0);
  };

  const calculatePercentage = (value, total) => {
    if (total === 0) return '0.0%';
    return ((value / total) * 100).toFixed(1) + '%';
  };

  const renderTable = (data, title, period, bgColor) => (
    <div className="analytics-table">
      <div className={`table-header ${bgColor}`}>
        <h3>{title}</h3>
        <span className="period">{period}</span>
      </div>
      <div className="table-content">
        <div className="metric-row">
          <span className="metric-label">TOTAL GMV</span>
          <span className="metric-value">{formatCurrency(data.totalGmv)}</span>
        </div>
        <div className="metric-row">
          <span className="metric-label">FLASH SALES PERFORMANCE</span>
          <span className="metric-value">{formatCurrency(data.flashSalesPerformance)}</span>
        </div>
        <div className="metric-row">
          <span className="metric-label">TOP-PERFORMING CREATORS</span>
          <span className="metric-value">{data.topPerformingCreators}</span>
        </div>
        <div className="metric-row">
          <span className="metric-label">GMV PER VIDEO</span>
          <span className="metric-value">{data.gmvpervideo}</span>
        </div>
        <div className="metric-row">
          <span className="metric-label">Refund Rate</span>
          <span className="metric-value">{data.refundrate}%</span>
        </div>
        <div className="metric-row">
          <span className="metric-label">Tiktok Campaign outcome</span>
          <span className="metric-value">{formatCurrency(data.Tiktokcompaign)}</span>
        </div>
      </div>
    </div>
  );

  const renderCustomRangeTable = () => {
    if (customRangeData.length === 0) return null;

    const metrics = [
      { key: 'totalGmv', label: 'TOTAL GMV', format: 'currency' },
      { key: 'flashSalesPerformance', label: 'FLASH SALES PERFORMANCE', format: 'currency' },
      { key: 'topPerformingCreators', label: 'TOP-PERFORMING CREATORS', format: 'number' },
      { key: 'gmvPerVideo', label: 'GMV PER VIDEO', format: 'number' },
      { key: 'refundRate', label: 'REFUND RATE', format: 'percentage' },
      { key: 'tiktokCampaign', label: 'TIKTOK CAMPAIGN OUTCOME', format: 'currency' }
    ];

    const formatValue = (value, format) => {
      switch (format) {
        case 'currency':
          return formatCurrency(value);
        case 'percentage':
          return `${value || 0}%`;
        case 'number':
        default:
          return value || 0;
      }
    };

    return (
      <div className="custom-range-table-container">
        <div className="table-header header-purple">
          <h3>Custom Date Range Analysis</h3>
          <span className="period">
            {dates.startDate} to {dates.endDate}
          </span>
        </div>
        <div className="custom-table-wrapper">
          <table className="custom-range-table">
            <thead>
              <tr>
                <th className="metric-header">Metrics</th>
                {customRangeData.map((dayData) => (
                  <th key={dayData.date} className="date-header">
                    {new Date(dayData.date).toLocaleDateString('en-US', {
                      month: 'short',
                      day: 'numeric'
                    })}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {metrics.map((metric) => (
                <tr key={metric.key}>
                  <td className="metric-label-cell">
                    <strong>{metric.label}</strong>
                  </td>
                  {customRangeData.map((dayData) => (
                    <td key={`${metric.key}-${dayData.date}`} className="metric-value-cell">
                      {formatValue(dayData[metric.key], metric.format)}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  return (
    <div className="dashboard-container">   
      <div className="tables-container">
        {renderTable(
          yesterdayData,
          'Yesterday',
          `Yesterday (${formatDate(new Date(Date.now() - 24 * 60 * 60 * 1000))})`,
          'header-blue'
        )}
        {renderTable(
          currentWeekData,
          'Current Week',
          `Week (${getStartOfWeek(new Date())} - ${formatDate(new Date())})`,
          'header-green'
        )}
        {renderTable(
          monthToDateData,
          'Month to date',
          `Month (${getStartOfMonth(new Date())} - ${formatDate(new Date())})`,
          'header-red'
        )}
      </div>

      {/* Date Picker and Custom Range Table Section */}
      <div className="custom-analysis-section">
        <div className="date-picker-section">
          <h2>Custom Date Range Analysis</h2>
          <DatePickerCard onDatesChange={setDates} />
          <button
            className="fetch-button"
            onClick={fetchPerformance}
            disabled={!dates.startDate || !dates.endDate || isLoading}
          >
            {isLoading ? 'Loading...' : 'Analyze Date Range'}
          </button>
        </div>

        {renderCustomRangeTable()}
      </div>
    </div>
  );
};

export default Dashboard;
