import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch users and their brands from the API
    axios.get('http://127.0.0.1:8000/admin/users')  // Adjust URL if deployed differently
      .then(response => {
        setUsers(response.data);  // Store the data in the state
      })
      .catch(error => {
        console.error("Error fetching users:", error);
      });
  }, []);

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      <table className="admin-dashboard-table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Brand Name</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.username}</td>  {/* Displaying username */}
              <td>{user.brand_name}</td>  {/* Displaying brand name */}
              <td>
                <Link to={`/admin/shop/${user.brand_id}`}>View Shop</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
