// src/components/ShopPerformance.jsx
import React, { useEffect, useState } from 'react';

const ShopPerformance = () => {
  const [shopData, setShopData] = useState({
    totalGMV: { daily: 5000, weekly: 35000, monthly: 150000 },
    campaignOutcomes: 'Positive growth with 20% increase in engagement.',
    refundRate: 5,
    flashSales: 'Great performance, 30% increase in sales.',
  });

  useEffect(() => {
    // Simulate fetching data (replace with actual API calls)
    const mockData = {
      totalGMV: { daily: 5000, weekly: 35000, monthly: 150000 },
      campaignOutcomes: 'Positive growth with 20% increase in engagement.',
      refundRate: 5,
      flashSales: 'Great performance, 30% increase in sales.',
    };

    setTimeout(() => {
      setShopData(mockData);
    }, 1000); // Simulate 1-second delay
  }, []);

  return (
    <div>
      <h2>Shop Performance Overview</h2>

      {/* Displaying Cards for GMV, Campaign Outcomes, Refund Rate, Flash Sales */}
      <div className="cards-container">
        {/* Total GMV Card */}
        <div className="card">
          <h3>Total GMV</h3>
          <div className="card-data">
            <p><strong>Daily:</strong> ${shopData.totalGMV.daily}</p>
            <div className="data-border"></div>
            <p><strong>Weekly:</strong> ${shopData.totalGMV.weekly}</p>
            <div className="data-border"></div>
            <p><strong>Monthly:</strong> ${shopData.totalGMV.monthly}</p>
          </div>
        </div>

        {/* TikTok Shop Campaign Outcomes Card */}
        <div className="card">
          <h3>TikTok Shop Campaign Outcomes</h3>
          <div className="card-data">
            <p>{shopData.campaignOutcomes}</p>
          </div>
        </div>

        {/* Refund Rate Card */}
        <div className="card">
          <h3>Refund Rate & Trends</h3>
          <div className="card-data">
            <p><strong>Refund Rate:</strong> {shopData.refundRate}%</p>
            <div className="data-border"></div>
            <p><strong>Trends:</strong> Stable with slight improvement in trends.</p>
          </div>
        </div>

        {/* Flash Sales Performance Card */}
        <div className="card">
          <h3>Flash Sales Performance</h3>
          <div className="card-data">
            <p>{shopData.flashSales}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShopPerformance;
