// src/components/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { fetchDashboardData } from '.../api/Api'; // Import the API function

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState({});

  useEffect(() => {
    // Fetch data using the function from Api.jsx
    const fetchData = async () => {
      try {
        const data = await fetchDashboardData();
        setDashboardData(data); // Update state with the data
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      }
    };

    fetchData(); // Call the function
  }, []);

  return (
    <div>
      <h2>Dashboard Overview</h2>
      <div className="kpi-details">
        <p>Total Sales: ${dashboardData.totalSales}</p>
        <p>Refund Rate: {dashboardData.refundRate}%</p>
        <p>Top SKUs: {dashboardData.topSKUs}</p>
        <p>Top Creators: {dashboardData.topCreators}</p>
        <p>Campaign ROI: {dashboardData.campaignROI}%</p>
      </div>
    </div>
  );
};

export default Dashboard;
