import React, { useEffect, useState } from 'react';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState({});

  useEffect(() => {
    // Mock data for now
    const mockData = {
      totalSales: 50000,
      refundRate: 5,
      topSKUs: 10,
      topCreators: 20,
      campaignROI: 25,
    };

    // Simulate an API call by using setTimeout or directly setting the mock data
    setTimeout(() => {
      setDashboardData(mockData); // Update the state with the mock data
    }, 1000); // Simulating a 1-second delay
  }, []);

  return (
    <div>
      <h2>Dashboard Overview</h2>
      <div className="kpi-details">
        <p>Total Sales: ${dashboardData.totalSales}</p>
        <p>Refund Rate: {dashboardData.refundRate}%</p>
        <p>Top SKUs: {dashboardData.topSKUs}</p>
        <p>Top Creators: {dashboardData.topCreators}</p>
        <p>Campaign ROI: {dashboardData.campaignROI}%</p>
      </div>
    </div>
  );
};

export default Dashboard;
