import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';
import axios from 'axios';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState({});

  useEffect(() => {
    // Fetch data from API
    axios.get('/api/dashboard').then((response) => {
      setDashboardData(response.data);
    });
  }, []);

  const chartData = {
    labels: ['Total Sales', 'Refund %', 'Top SKUs', 'Top Creators', 'Campaign ROI'],
    datasets: [
      {
        label: 'KPIs',
        data: [
          dashboardData.totalSales || 0,
          dashboardData.refundRate || 0,
          dashboardData.topSKUs || 0,
          dashboardData.topCreators || 0,
          dashboardData.campaignROI || 0,
        ],
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div>
      <h2>Dashboard Overview</h2>
      <Bar data={chartData} />
      <div className="kpi-details">
        <p>Total Sales: ${dashboardData.totalSales}</p>
        <p>Refund Rate: {dashboardData.refundRate}%</p>
        <p>Top SKUs: {dashboardData.topSKUs}</p>
        <p>Top Creators: {dashboardData.topCreators}</p>
        <p>Campaign ROI: {dashboardData.campaignROI}%</p>
      </div>
    </div>
  );
};

export default Dashboard;
