    alertTitle: {
      display: 'flex',
      alignItems: 'center',
      fontSize: '12px',
      color: '#92400e',
      marginBottom: '4px',
      gap: '4px'
    },
    alertContent: {
      fontSize: '12px',
      color: '#a16207'
    },
    bottomSection: {
      padding: '16px',
      borderTop: '1px solid #e5e7eb'
    }
  };

  // Backend Metrics dropdown items
  const backendMetricsItems = [
    {
      id: 'inventory-tracker',
      name: 'Inventory Tracker',
      href: '/backend-metrics/inventory',
      icon: Warehouse,
