import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Sidebar from './components/Sidebar'; // Import Sidebar
import Dashboard from './components/Dashboard'; // Dashboard component
import ShopPerformance from './components/ShopPerformance'; // ShopPerformance component (mock for now)
import CreatorPerformance from './components/CreatorPerformance'; // CreatorPerformance component (mock for now)
import './components/Sidebar.css'; // Import sidebar styles

const App = () => {
  return (
    <Router>
      <div style={{ display: 'flex' }}>
        <Sidebar /> {/* Add the sidebar here */}
        <div style={{ marginLeft: '250px', padding: '20px', width: '100%' }}>
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/shop-performance" element={<ShopPerformance />} />
            <Route path="/creator-performance" element={<CreatorPerformance />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
